import React from "react";
import "./input.css";

function Input(props) {
  return (
    <div
      className={`${classes.control} ${
        props.isvaild === false ? classes.invalid : ""
      }`}
    >
      <label htmlFor={props.id}>E-Mail</label>
      <input
        type={props.type}
        id={props.id}
        value={props.value}
        onChange={props.onChange}
        onBlur={props.on}
      />
    </div>
  );
}

export default Input;
